class CurveShape(object):

    def __init__(self, shape):
        self.shape = pm.PyNode(shape)

    def get_shape_cmd(self, mirror=False, scale=1.0):
        degree = self.get_degree()
        periodic = self.get_periodic()
        cps, knots = self.get_cps_knots(mirror, scale)
        str_cps = ",".join(str(e) for e in cps)
        str_knots = ",".join(str(e) for e in knots)

        return "pm.curve(d=%s, per=%s, p=[%s], k=[%s])" % (degree, periodic, str_cps, str_knots)

    def reset_shape(self):
        for attr in ["xValue", "yValue", "zValue"]:
            for cp in cmds.ls("%s.controlPoints[*].%s" % (self.shape.name(), attr)):
                cmds.setAttr(cp, 0)

    def set_shape(self, cmd):
        curve = eval(cmd)
        pm.connectAttr(curve.getShape().worldSpace[0], self.shape.create, f=True)
        self.reset_shape()
        pm.refresh(cv=True)
        pm.delete(curve)
        return

    def set_shape_from_library(self, shape, axis="+x"):
        assert shape in shape_library, "Shape %s Not Found" % shape

        rotation_dict = {"+x": [0, 0, -90],
                         "-x": [0, 0, 90],
                         "+y": [0, 0, 0],
                         "-y": [0, 180, 0],
                         "+z": [90, 0, 00],
                         "-z": [-90, 0, 0]}

        curve = eval(shape_library[shape])
        pm.xform(curve, ro=rotation_dict[axis])
        pm.makeIdentity(curve, apply=1, r=True, t=True, s=True)

        pm.connectAttr(curve.getShape().worldSpace[0], self.shape.create)
        pm.refresh(cv=True)
        pm.delete(curve)

        return

    def get_degree(self):
        return cmds.getAttr("%s.degree" % self.shape)

    def get_periodic(self):
        return cmds.getAttr("%s.form" % self.shape) == 2

    def get_cps_knots(self, mirror, scale):
        is_default_scale = scale == 1.0

        grp = cmds.group(empty=True, name="temp")
        cmds.parent(self.shape.name(), grp, s=True, r=True, add=True)

        if not is_default_scale:
            clus, cluster_handle = cmds.cluster(grp)
            cmds.setAttr("%s.scale" % cluster_handle, scale, scale, scale)

        if mirror:
            cmds.setAttr("%s.scaleX" % grp, -1)
            cmds.setAttr("%s.scaleY" % grp, -1)

        info = cmds.createNode("curveInfo")
        cmds.connectAttr("%s.worldSpace[1]" % self.shape, "%s.inputCurve" % info)
        cps = cmds.getAttr("%s.cp[*]" % info)
        knots = cmds.getAttr("%s.knots[*]" % info)

        if not is_default_scale:
            cmds.setAttr("%s.envelope" % clus, 0)
            cmds.refresh(cv=True)
            cmds.delete(cluster_handle)

        cmds.delete(info, grp)

        return cps, knots
